#include <iostream>#include <vector>#include <string>using namespace std; string min_lex(vector<string>& words) {    string s = "";    for (auto& w : words) {        string option1 = w + s; // prepend        string option2 = s + w; // append        s = min(option1, option2); // choose the smaller lexicographically    }    return s;} int main() {        int t;    cin >> t;    while (t--) {        int n;        cin >> n;        vector<string> words(n);        for (int i = 0; i < n; i++) {            cin >> words[i];        }        cout << min_lex(words) << endl;    }    return 0;}